var canvas=document.querySelector("canvas");function canvasResize(){return canvasWidth=window.offsetWidth,canvas.width=window.innerWidth,canvas.height=window.innerHeight,canvasWidth}canvasResize(),window.addEventListener("resize",canvasResize,!1);var ctx=canvas.getContext("2d");function Circle(i,t,r,a,s){this.x=i,this.y=t,this.dx=r,this.dy=a,this.radius=s,this.draw=function(){ctx.beginPath(),ctx.arc(this.x,this.y,this.radius,0,2*Math.PI,!1),ctx.strokeStyle="rgba(255,255,255,.31)",ctx.lineWidth=2,ctx.stroke()},this.update=function(){(this.x+this.radius>innerWidth||this.x-this.radius<0)&&(this.dx=-this.dx),(this.y+this.radius>innerHeight||this.y-this.radius<0)&&(this.dy=-this.dy),this.x+=this.dx,this.y+=this.dy,this.draw()}}var circleArray=[],circles=(window.innerWidth-window.innerHeight)/12;circles<0?circles=20:40<circles&&(circles=40);for(var i=0;i<circles;i++){var radius=i/2*5,x=Math.random()*(innerWidth-2*radius)+radius,y=Math.random()*(innerHeight-2*radius)+radius,dx=Math.random()-.5,dy=Math.random()-.5;circleArray.push(new Circle(x,y,dx,dy,radius))}function animate(){requestAnimationFrame(animate),ctx.clearRect(0,0,innerWidth,innerHeight);for(var i=0;i<circleArray.length;i++)circleArray[i].update()}animate();